mapper.update:
update chatmember
set
msg_count = msg_count + 1
where useriid != #{userid}
and chat_no = #{chat_no}

mapper.select:
	select A.*, B.msg_count from chatgroup A, chatmember B
	where A.st_no = B.st_no and
    B.userid = #{userid}

mapper.update:
update chatmember
set
msg_count = 0
where userid = #{userid}
and chat_no = #{chat_no}

mapper.select:
select count(*) as cnt
from chatmember
where chat_no = #{chat_no}

mapper.update:
update chatmessage
set unread_count = unread_count - 1
where chat_no = #{chat_no}
and message_id = #{message_id}

mapper.delete:
delete from unreadmember
where chat_no = #{chat_no}
and userid = #{userid}
